<template>
  <div class="login-container">
    <el-form ref="loginForm" :model="loginForm" :rules="loginRules" class="login-form" autocomplete="on" label-position="left">

      <div class="title-container">
        <h3 class="title">用户登录</h3>
      </div>

      <el-form-item prop="username">
        <span class="svg-container">
          <svg-icon icon-class="user" />
        </span>
        <el-input
          ref="username"
          v-model="loginForm.username"
          placeholder="用户名"
          name="username"
          type="text"
          tabindex="1"
          autocomplete="on"
        />
      </el-form-item>

      <el-tooltip v-model="capsTooltip" content="Caps lock is On" placement="right" manual>
        <el-form-item prop="password">
          <span class="svg-container">
            <svg-icon icon-class="password" />
          </span>
          <el-input
            :key="passwordType"
            ref="password"
            v-model="loginForm.password"
            :type="passwordType"
            placeholder="密码"
            name="password"
            tabindex="2"
            autocomplete="on"
            @keyup.native="checkCapslock"
            @blur="capsTooltip = false"
            @keyup.enter.native="handleLogin"
          />
          <span class="show-pwd" @click="showPwd">
            <svg-icon :icon-class="passwordType === 'password' ? 'eye' : 'eye-open'" />
          </span>
        </el-form-item>
      </el-tooltip>
      <!--滑动解锁-->
      <div class="drag_verify" :style="dragVerifyStyle" @mousemove="dragMoving" @mouseup="dragFinish" @touchmove="dragMoving" @touchend="dragFinish">
        <div class="dv_progress_bar" ref="progressBar" :style="progressBarStyle"></div>
        <div class="dv_text" :style="textStyle" ref="message">{{message}}</div>
        <div class="dv_handler dv_handler_bg" @mousedown="dragStart"  ref="handler" :style="handlerStyle" @touchstart="dragStart"><i :class="handlerIcon"></i></div>
      </div><br>
      <el-button :loading="loading" type="primary" style="width:100%;margin-bottom:30px;" @click.native.prevent="handleLogin">登录</el-button>
      <div style="position:relative">
        <br><br><br>
        <el-button class="thirdparty-button" type="primary" @click="showDialog=true">
          第三方登录
        </el-button>
      </div>
    </el-form>
  </div>
</template>

<script>
import { validUsername } from '@/utils/validate'

export default {
  name: 'Login',
  props:{
    width:{
      type:Number,
      default:450
    },
    height:{
      type:Number,
      default:50
    },
    text:{
      type:String,
      default:'向右滑动进行登陆'
    },
    successText:{
      type:String,
      default:'登陆成功'
    },
    background:{
      type:String,
      default:'#ccc'
    },
    progressBarBg:{
      type:String,
      default:'#FFFF99'
    },
    completedBg:{
      type:String,
      default:'#66cc66'
    },
    circle:{
      type:Boolean,
      default: true
    },
    handlerIcon:{
      type:String

    },
    successIcon:{
      type:String
    },
    handlerBg:{
      type:String,
      default:'#fff'
    },
    textSize:{
      type:String,
      default:'20px'
    }
  },
  computed:{
    handlerStyle:function(){
      return {
        left: '0px',
        width : this.height-2+'px',
        height : this.height-2+'px',
        borderRadius: this.circle?'50%':0,
        background: this.handlerBg
      }
    },
    message : function(){
      return this.isPassing?this.successText:this.text
    },
    dragVerifyStyle:function(){
      return {
        width: this.width + 'px',
        height : this.height+'px',
        lineHeight: this.height+'px',
        background:this.background,
        borderRadius: this.circle?this.height/2+'px':0
      }
    },
    progressBarStyle: function(){
      return {
        background: this.progressBarBg,
        height : this.height+'px',
        borderRadius: this.circle?this.height/2+'px 0 0 ' + this.height/2+'px':0
      }
    },
    textStyle: function(){
      return {
        height : this.height+'px',
        width : this.width + 'px',
        fontSize:this.textSize
      }
    },
    handlerIconClass: function(){
      return this.isPassing?this.handlerIcon:this.successIcon
    }

  },
  data() {
    const validateUsername = (rule, value, callback) => {
      if (!validUsername(value)) {
        callback(new Error('请输入正确的用户名！'))
      } else {
        callback()
      }
    }
    const validatePassword = (rule, value, callback) => {
      if(value.length > 12){
        callback(new Error('密码最多只能12位！'))
      }
      if (value.length < 5) {
        callback(new Error('密码不能少于6位！'))
      } else {
        callback()
      }
    }
    return {
      isMoving : false,
      x:0,
      isPassing : false,
      loginForm: {
        username: '',
        password: ''
      },
      loginRules: {
        username: [{ required: true, trigger: 'blur', message:'请输入用户名！' }],
        password: [{ required: true, trigger: 'blur', validator: validatePassword }]
      },
      passwordType: 'password',
      capsTooltip: false,
      loading: false,
      redirect: undefined,
      otherQuery: {}
    }
  },
  watch: {
    $route: {
      handler: function(route) {
        const query = route.query
        if (query) {
          this.redirect = query.redirect
          this.otherQuery = this.getOtherQuery(query)
        }
      },
      immediate: true
    }
  },
  created() {
    // window.addEventListener('storage', this.afterQRScan)
  },
  mounted() {
    if (this.loginForm.username === '') {
      this.$refs.username.focus()
    } else if (this.loginForm.password === '') {
      this.$refs.password.focus()
    }
  },
  destroyed() {
    // window.removeEventListener('storage', this.afterQRScan)
  },
  methods: {
    dragStart: function(e){
      if(!this.isPassing) {
        this.isMoving = true;
        var handler = this.$refs.handler;
        this.x = (e.pageX||e.touches[0].pageX) - parseInt(handler.style.left.replace('px',''), 10);
      }

    },
    dragMoving: function(e){
      if(this.isMoving && !this.isPassing){
        var _x = (e.pageX||e.touches[0].pageX) - this.x;
        var handler = this.$refs.handler;
        if(_x > 0 && _x <= (this.width-this.height)){
          handler.style.left = _x + 'px';
          this.$refs.progressBar.style.width = (_x+this.height/2)+'px';
        }else if(_x > (this.width-this.height)){
          handler.style.left = (this.width - this.height)+ 'px';
          this.$refs.progressBar.style.width = (this.width-this.height/2)+'px';
          this.passVerify();
        }
      }
    },
    dragFinish: function(e){
      if(this.isMoving && !this.isPassing){
        var _x = (e.pageX || e.changedTouches[0].pageX)- this.x;
        if(_x <(this.width - this.height)){
          this.$refs.handler.style.left = '0';
          this.$refs.progressBar.style.width = '0';
        }
        this.isMoving = false;
      }
    },
    passVerify: function(){
      this.handleLogin(); // 滑动登陆
      this.isPassing = true;
      this.isMoving = false;
      var handler = this.$refs.handler;
      handler.className += ' dv_handler_ok_bg';
      handler.children[0].className = this.successIcon;
      this.$refs.progressBar.style.background = this.completedBg;
      this.$refs.message.style.color = '#fff';
      this.$emit('passcallback');
    },
    checkCapslock({ shiftKey, key } = {}) {
      if (key && key.length === 1) {
        if (shiftKey && (key >= 'a' && key <= 'z') || !shiftKey && (key >= 'A' && key <= 'Z')) {
          this.capsTooltip = true
        } else {
          this.capsTooltip = false
        }
      }
      if (key === 'CapsLock' && this.capsTooltip === true) {
        this.capsTooltip = false
      }
    },
    showPwd() {
      if (this.passwordType === 'password') {
        this.passwordType = ''
      } else {
        this.passwordType = 'password'
      }
      this.$nextTick(() => {
        this.$refs.password.focus()
      })
    },
    handleLogin() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true
          this.$store.dispatch('user/login', this.loginForm)
            .then(() => {
              // 根据路由进行跳转了
              console.debug(this.redirect)
              this.$router.push({ path: this.redirect || '/', query: this.otherQuery })
              this.loading = false
            })
            .catch(() => {
              this.loading = false
            })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    getOtherQuery(query) {
      return Object.keys(query).reduce((acc, cur) => {
        if (cur !== 'redirect') {
          acc[cur] = query[cur]
        }
        return acc
      }, {})
    }
    // afterQRScan() {
    //   if (e.key === 'x-admin-oauth-code') {
    //     const code = getQueryObject(e.newValue)
    //     const codeMap = {
    //       wechat: 'code',
    //       tencent: 'code'
    //     }
    //     const type = codeMap[this.auth_type]
    //     const codeName = code[type]
    //     if (codeName) {
    //       this.$store.dispatch('LoginByThirdparty', codeName).then(() => {
    //         this.$router.push({ path: this.redirect || '/' })
    //       })
    //     } else {
    //       alert('第三方登录失败')
    //     }
    //   }
    // }
  }
}
</script>
<style>
  .drag_verify{
    position: relative;
    background-color: #e8e8e8;
    text-align: center;

  }
  .drag_verify .dv_handler{
    position: absolute;
    top: 0px;
    left: 0px;
    border: 1px solid #ccc;
    cursor: move;
  }
  .drag_verify .dv_handler_bg{
    /* background: #fff url("data:image/png;base64,") no-repeat center;*/
  }
  .drag_verify .dv_handler_ok_bg{
    /* background: #fff url("data:image/png;base64,") no-repeat center;*/
  }
  .drag_verify .dv_handler i{
    color: #666;
    font-size: 1.5em;
  }
  .drag_verify .dv_progress_bar{
    position: absolute;
    height: 34px;
    width: 0px;
    transition: background 2s ease-in;
  }
  .drag_verify .dv_text{
    position: absolute;
    top: 0px;
    color:#444;
    -moz-user-select: none;
    -webkit-user-select: none;
    user-select: none;
    -o-user-select:none;
    -ms-user-select:none;
  }
</style>
<style lang="scss">
/* 修复input 背景不协调 和光标变色 */
/* Detail see https://github.com/PanJiaChen/vue-element-admin/pull/927 */

$bg:#283443;
$light_gray:#fff;
$cursor: #fff;

@supports (-webkit-mask: none) and (not (cater-color: $cursor)) {
  .login-container .el-input input {
    color: $cursor;
  }
}

/* reset element-ui css */
.login-container {
  .el-input {
    display: inline-block;
    height: 47px;
    width: 85%;

    input {
      background: transparent;
      border: 0px;
      -webkit-appearance: none;
      border-radius: 0px;
      padding: 12px 5px 12px 15px;
      color: $light_gray;
      height: 47px;
      caret-color: $cursor;

      &:-webkit-autofill {
        box-shadow: 0 0 0px 1000px $bg inset !important;
        -webkit-text-fill-color: $cursor !important;
      }
    }
  }

  .el-form-item {
    border: 1px solid rgba(255, 255, 255, 0.1);
    background: rgba(0, 0, 0, 0.1);
    border-radius: 5px;
    color: #454545;
  }
}
</style>

<style lang="scss" scoped>
$bg:#2d3a4b;
$dark_gray:#889aa4;
$light_gray:#eee;

.login-container {
  min-height: 100%;
  width: 100%;
  background-color: $bg;
  overflow: hidden;

  .login-form {
    position: relative;
    width: 520px;
    max-width: 100%;
    padding: 160px 35px 0;
    margin: 0 auto;
    overflow: hidden;
  }

  .tips {
    font-size: 14px;
    color: #fff;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .title-container {
    position: relative;

    .title {
      font-size: 26px;
      color: $light_gray;
      margin: 0px auto 40px auto;
      text-align: center;
      font-weight: bold;
    }
  }

  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }

  .thirdparty-button {
    position: absolute;
    right: 0;
    bottom: 6px;
  }

  @media only screen and (max-width: 470px) {
    .thirdparty-button {
      display: none;
    }
  }
}
</style>
